\nimport \'package:flutter/material.dart\';\n\nclass DashboardScreen extends StatelessWidget {\n  const DashboardScreen({super.key});\n\n  @override\n  Widget build(BuildContext context) {\n    return ListView(\n      padding: const EdgeInsets.all(16.0),\n      children: [\n        _buildSummaryCard(),\n        const SizedBox(height: 20),\n        _buildGoalsCard(),\n        const SizedBox(height: 20),\n        _buildTransactionsCard(),\n      ],\n    );\n  }\n\n  Widget _buildSummaryCard() {\n    return Card(\n      child: Padding(\n        padding: const EdgeInsets.all(16.0),\n        child: Column(\n          crossAxisAlignment: CrossAxisAlignment.start,\n          children: [\n            Text(\'Financial Summary\', style: const TextStyle(fontSize: 20, fontWeight: FontWeight.bold)),\n            const SizedBox(height: 10),\n            Text(\'Total Balance: \$10,000\'),\n            Text(\'Total Debt: \$2,500\'),\n          ],\n        ),\n      ),\n    );\n  }\n\n  Widget _buildGoalsCard() {\n    return Card(\n      child: Padding(\n        padding: const EdgeInsets.all(16.0),\n        child: Column(\n          crossAxisAlignment: CrossAxisAlignment.start,\n          children: [\n            Text(\'Financial Goals\', style: const TextStyle(fontSize: 20, fontWeight: FontWeight.bold)),\n            const SizedBox(height: 10),\n            Text(\'Retirement: \$50,000 / \$500,000\'),\n            Text(\'New Car: \$10,000 / \$25,000\'),\n          ],\n        ),\n      ),\n    );\n  }\n\n  Widget _buildTransactionsCard() {\n    return Card(\n      child: Padding(\n        padding: const EdgeInsets.all(16.0),\n        child: Column(\n          crossAxisAlignment: CrossAxisAlignment.start,\n          children: [\n            Text(\'Recent Transactions\', style: const TextStyle(fontSize: 20, fontWeight: FontWeight.bold)),\n            const SizedBox(height: 10),\n            ListTile(\n              leading: Icon(Icons.shopping_cart),\n              title: Text(\'Groceries\'),\n              trailing: Text(\'-\$150\'),\n            ),\n            ListTile(\n              leading: Icon(Icons.attach_money),\n              title: Text(\'Paycheck\'),\n              trailing: Text(\'+\$2,000\'),\n            ),\n          ],\n        ),\n      ),\n    );\n  }\n}\n